def sum_of_divisors(n):
    result = 0
    i = 1
    while i <= n:
        if (n % i==0):
            result = result + i
        i = i + 1
    return result

def is_prime(n):
    if n <= 1:
        return False
    if n <= 3:
        return True
    if n%2 == 0 or n%3 == 0:
        return False
    i = 5
    while(i * i <= n):
        if (n%i == 0 or n%(i + 2) == 0):
            return False
        i += 6
    return True

count = 0
for n in range(1, 26):
    if is_prime(sum_of_divisors(n)):
        count += 1

print(count)

